class PrintMatrixOp : public mlir::Op {
public:
  using Op::Op;
  static llvm::StringRef getOperationName() { return "memory.print"; }

  static void build(mlir::OpBuilder &builder, mlir::OperationState &state,
                    mlir::Value memref) {
    state.addOperands({memref});
  }

  static llvm::ArrayRef getAttributeNames() {
    return {};
  }

  void print(mlir::OpAsmPrinter &p) {
    p getOperation()->getOperand(0) 
      getOperation()->getOperand(0).getType();
  }

  static mlir::ParseResult parse(mlir::OpAsmParser &parser, 
                                mlir::OperationState &result) {
    mlir::OpAsmParser::UnresolvedOperand memref;
    mlir::Type memrefType;

    if (parser.parseOperand(memref) ||
        parser.parseColon() ||
        parser.parseType(memrefType))
      return mlir::failure();

    if (parser.resolveOperand(memref, memrefType, result.operands))
      return mlir::failure();

    return mlir::success();
  }
};

