#include "MemoryPass.h"
#include "MemoryDialect.h"
#include "mlir/IR/PatternMatch.h"
#include "mlir/Dialect/MemRef/IR/MemRef.h"
#include "mlir/Dialect/Arithmetic/IR/Arithmetic.h"

using namespace mlir;

namespace memory {

namespace {

struct MemoryToMemRefPass : public PassWrapper> {

  StringRef getArgument() const final {
    return "convert-memory-to-memref";
  }

  StringRef getDescription() const final {
    return "Convert memory dialect operations to standard memref operations";
  }

  void runOnOperation() override {
    SmallVector createOps;
    SmallVector setOps;  
    SmallVector getOps;
    SmallVector printOps;

    getOperation().walk([&](Operation *op) {
      if (auto createOp = dyn_cast(op))
        createOps.push_back(createOp);
      else if (auto setOp = dyn_cast(op))
        setOps.push_back(setOp);
      else if (auto getOp = dyn_cast(op))
        getOps.push_back(getOp);
      else if (auto printOp = dyn_cast(op))
        printOps.push_back(printOp);
    });

    // 转换 CreateMatrixOp -> memref.alloc
    for (auto op : createOps) {
      OpBuilder builder(op);

      auto resultType = op->getResult(0).getType().cast();
      auto operands = op->getOperands();

      auto allocOp = builder.create(
          op.getLoc(), resultType, operands);

      op->getResult(0).replaceAllUsesWith(allocOp.getResult());
      op->erase();

      llvm::outs()  memref.store
    for (auto op : setOps) {
      OpBuilder builder(op);
      auto operands = op->getOperands();

      Value memref = operands[0];
      Value i = operands[1]; 
      Value j = operands[2];
      Value value = operands[3];

      builder.create(
          op.getLoc(), value, memref, ValueRange{i, j});

      op->erase();
      llvm::outs()  memref.load
    for (auto op : getOps) {
      OpBuilder builder(op);
      auto operands = op->getOperands();

      Value memref = operands[0];
      Value i = operands[1];
      Value j = operands[2];

      auto loadOp = builder.create(
          op.getLoc(), memref, ValueRange{i, j});

      op->getResult(0).replaceAllUsesWith(loadOp.getResult());
      op->erase();

      llvm::outs() erase();
    }
  }
};

} // namespace

std::unique_ptr createMemoryToMemRefPass() {
  return std::make_unique();
}

} // namespace memory

