MyPass.cpp
#include "MyPass.h"
#include "MyPatterns.h"

#include "mlir/Dialect/Func/IR/FuncOps.h"
#include "mlir/Pass/Pass.h"
#include "mlir/Transforms/GreedyPatternRewriteDriver.h"
#include "mlir/IR/PatternMatch.h"

using namespace mlir;

namespace {
struct RemoveMyAddZeroPass
    : public PassWrapper> {

    virtual ~RemoveMyAddZeroPass() = default;

    StringRef getArgument() const final { return "remove-my-add-zero"; }
    StringRef getDescription() const final { return "Fold arith.addi with zero"; }

    void runOnOperation() override {
        func::FuncOp func = getOperation();
        MLIRContext *ctx = &getContext();

        RewritePatternSet patterns(ctx);
        patterns.add(ctx);

        if (failed(applyPatternsGreedily(func, std::move(patterns))))
            signalPassFailure();
    }
};
}

std::unique_ptr mlir::createRemoveMyAddZeroPass() {
    return std::make_unique();
}

void mlir::registerRemoveMyAddZeroPass() {
    PassRegistration();
}

MyPatterns.h
#ifndef MY_PATTERNS_H
#define MY_PATTERNS_H

#include "mlir/Dialect/Arith/IR/Arith.h"
#include "mlir/IR/PatternMatch.h"

struct RemoveAddZeroPattern : public mlir::OpRewritePattern {
  using OpRewritePattern::OpRewritePattern;

  mlir::LogicalResult matchAndRewrite(mlir::arith::AddIOp op,
                                mlir::PatternRewriter &rewriter) const override {
    mlir::Value lhs = op.getLhs();
    mlir::Value rhs = op.getRhs();

    auto isZeroConst = [](mlir::Value val) -> bool {
      if (auto cst = val.getDefiningOp()) {
        if (auto attr = mlir::dyn_cast(cst.getValue()))
          return attr.getValue().isZero();
      }
      return false;
    };

    if (isZeroConst(rhs)) {
      rewriter.replaceOp(op, lhs);
      return mlir::success();
    }

    if (isZeroConst(lhs)) {
      rewriter.replaceOp(op, rhs);
      return mlir::success();
    }

    return mlir::failure();
  }
};

#endif

